import React, { useState } from 'react';
import BlogDetails from '../components/BlogDetails';

export default function Blog() {
    const [articleOpen, setArticle] = useState(false)

     /* this function toggles the booking detail component */
     const toggleArticle = (article) => {
        setSelectedArticle(article);
        setArticle(!articleOpen);
    }

    const [selectedArticle, setSelectedArticle] = useState({})

    let data = [
        {
            title: "Blog Post 1",
            description: "This is my first blog post!",
            img: './assets/boatOnRiver.jpg',
            id: 1
        },
        {
            title: "Blog Post 2",
            description: "This is my second blog post!",
            img: './assets/girlInAsia.jpg',
            id: 2
        },
        {
            title: "Blog Post 3",
            description: "This is my third blog post!",
            img: './assets/girlFeedFish.jpg',
            id: 3
        }
    ]
    return (
        <div>
            {articleOpen && ( 
                <>
                    <div className="booking-close" onClick={() => toggleArticle({})}>Close</div>
                    <BookingDetail selectedArticle={selectedArticle} />
                </>
            )}
            <div>
                <h1>Blog</h1>
                <p>Welcome to my blog!</p>
                <div className='article-container'>
                {data.map((article) => {
                    return (
                        <div className='article'>
                            <img src={article.img} alt='article' />
                            <p className='article-title'>{article.title}</p>
                            <p className='article-description'>{article.description}</p>
                        </div>
                    )
                })}
                </div>
            </div>
        </div>
    )
}
