import { useState, useEffect } from "react";

export default function Head() {
    const [currentSlide, setCurrentSlide] = useState(0);
    const slides = [
        {image: "../assets/birdEyeOcean.jpg", caption: "Bird's Eye View of the Ocean"},
        {image: "../assets/familyInAirport.jpg", caption: "Family in Airport"},
        {image: "../assets/girlLookAtMap.jpg", caption: "Girl Looking at Map"}
    ];
    
    const nextSlide = () => {
        setCurrentSlide((currentSlide + 1) % slides.length);
    };

    const prevSlide = () => {
        setCurrentSlide((currentSlide - 1 + slides.length) % slides.length);
    };

    useEffect(() => {
        const timer = setInterval(nextSlide, 3000); // Change slide every 3 seconds
        return () => clearInterval(timer); // Clear interval on component unmount
    }, [currentSlide]);

    return (
        <div className="slideshow-container">
            <div className="slide fade">
                <img src={slides[currentSlide].image}></img>
                <div className="caption">{slides[currentSlide].caption}</div>
            </div>
            <button className="next" onClick={nextSlide}>&#10095;</button>
            <button className="prev" onClick={prevSlide}>&#10094;</button>
        </div>
    );
}
